class Prices extends React.Component {
    state = {
        currency : 'USD'
    }

    render() {
        let list = '';
        if(this.state.currency === 'USD'){
         list =  <li className="list-group-item">
            Bitcoin rate for {this.props.bpi.description}
            : <span class="badge-primary">
                {this.props.bpi.USD.code}
            </span> <strong>{this.props.bpi.USD.rate}</strong>
        </li>

        } else if(this.state.currency === 'INR'){
            list =  <li className="list-group-item">
            Bitcoin rate for {this.props.bpi.description}
            : <span class="badge-primary">
                {this.props.bpi.USD.code}
            </span> <strong>{this.props.bpi.INR.rate}</strong>
        </li>

        } else if(this.state.currency === 'EUR') {
            list =  <li className="list-group-item">
            Bitcoin rate for {this.props.bpi.description}
            : <span class="badge-primary">
                {this.props.bpi.USD.code}
            </span> <strong>{this.props.bpi.EUR.rate}</strong>
        </li>

        }

        return (
        <div>
            <ul className="list-group">
                {}
            </ul>
        </div>);
    }
}

export default Prices;
